import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Box, Typography, TextField, Button, Grid, Container, Alert, Paper } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import SvgIcon from '@mui/material/SvgIcon';
import { authService } from '../services/api';
import './Login.css';

// Custom EA logo icon component
const EALogoIcon = (props) => {
  return (
    <SvgIcon {...props} viewBox="0 0 24 24">
      <path 
        d="M12 2L4 6v12l8 4 8-4V6L12 2zm0 2.5L18 8v8l-6 3-6-3V8l6-3.5zm1 1.5h-2v6h2V6zm0 8h-2v2h2v-2z"
        fill="currentColor"
      />
    </SvgIcon>
  );
};

const Login = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    try {
      // In a real application, this would call the API
      // For now we'll just simulate a successful login
      console.log('Attempting login with:', formData);
      
      // Uncomment this when your backend is ready
      /*
      const response = await authService.login(formData);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      */
      
      // For demonstration purposes
      setTimeout(() => {
        localStorage.setItem('token', 'sample-token');
        localStorage.setItem('user', JSON.stringify({ id: 1, email: formData.email }));
        navigate('/dashboard');
        setLoading(false);
      }, 1000);
      
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred during login');
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-content">
        <Container maxWidth="xs">
          <Box sx={{ 
            display: 'flex', 
            flexDirection: 'column', 
            alignItems: 'center',
            mt: 8 
          }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
              <div className="auth-logo">
                <EALogoIcon sx={{ fontSize: 36, color: theme.palette.primary.main }} />
              </div>
              <Typography 
                variant="h5" 
                component="div" 
                sx={{ 
                  ml: 1.5, 
                  fontWeight: 700,
                  background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.primary.light})`,
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent'
                }}
              >
                EventAlly
              </Typography>
            </Box>

            <Paper 
              elevation={3} 
              sx={{ 
                p: 4, 
                width: '100%', 
                borderRadius: 3,
                backgroundColor: 'rgba(255, 255, 255, 0.9)',
                backdropFilter: 'blur(10px)'
              }}
            >
              <Typography variant="h5" align="center" sx={{ mb: 3, fontWeight: 600, color: theme.palette.primary.main }}>
                Sign In
              </Typography>
              
              {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}
              
              <form onSubmit={handleSubmit}>
                <Grid container spacing={3}>
                  <Grid item xs={12}>
                    <TextField
                      name="email"
                      label="Email"
                      type="email"
                      variant="outlined"
                      fullWidth
                      required
                      value={formData.email}
                      onChange={handleChange}
                      InputLabelProps={{
                        style: { color: theme.palette.text.secondary }
                      }}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      name="password"
                      label="Password"
                      type="password"
                      variant="outlined"
                      fullWidth
                      required
                      value={formData.password}
                      onChange={handleChange}
                      InputLabelProps={{
                        style: { color: theme.palette.text.secondary }
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sx={{ textAlign: 'right' }}>
                    <Link to="/forgot-password" style={{ 
                      color: theme.palette.primary.main, 
                      textDecoration: 'none',
                      fontWeight: 500,
                      fontSize: '0.875rem'
                    }}>
                      Forgot password?
                    </Link>
                  </Grid>
                  <Grid item xs={12}>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      fullWidth
                      size="large"
                      sx={{ 
                        mt: 2, 
                        py: 1.5, 
                        borderRadius: '50px',
                        textTransform: 'none',
                        fontWeight: 600
                      }}
                      disabled={loading}
                      disableElevation
                    >
                      {loading ? 'Signing in...' : 'Sign In'}
                    </Button>
                  </Grid>
                </Grid>
              </form>
              
              <Box sx={{ mt: 3, textAlign: 'center' }}>
                <Typography variant="body2">
                  Don't have an account? <Link to="/register" style={{ 
                    color: theme.palette.primary.main, 
                    textDecoration: 'none',
                    fontWeight: 500
                  }}>Register</Link>
                </Typography>
              </Box>
            </Paper>
          </Box>
        </Container>
      </div>
    </div>
  );
};

export default Login;
